import 'package:flutter/material.dart';

import '../constants/themes.dart';

class RoundedButton extends StatelessWidget {
  const RoundedButton({
    super.key,
    required this.size,
    this.backgroundColor = AppTheme.appThemeColor,
    this.labelColor = Colors.white,
    required this.label,
    this.bordered = false,
  });

  final Size size;
  final Color backgroundColor;
  final Color labelColor;
  final String label;
  final bool bordered;

  @override
  Widget build(BuildContext context) {
    return Container(
      width: size.width,
      height: 45.0,
      decoration: BoxDecoration(
        borderRadius: BorderRadius.circular(16.0),
        color: bordered ? null : backgroundColor,
        border:
            bordered ? Border.all(color: backgroundColor, width: 1.0) : null,
        boxShadow: bordered
            ? null
            : [
                AppBoxShadow.s0BoxShadow(color: Colors.black.withOpacity(0.056))
              ],
      ),
      child: Center(
        child: Text(label,
            style: AppTextStyle.h4TextStyle(
                color: labelColor, fontWeight: FontWeight.w500)),
      ),
    );
  }
}
